mapper.select:
SELECT COUNT(reservation_no) cnt
FROM   reservation
WHERE  care_no = #{value}

mapper.update:
UPDATE reservation
SET status = 'true'
WHERE reservation_no = #{value}

UPDATE reservation
SET status = 'false'
WHERE reservation_no = #{value}

mapper.select:
SELECT COUNT(reservation_no) cnt
FROM   reservation
WHERE  member_id = #{value}

SELECT SUM(PEOPLE)
FROM RESERVATION
WHERE CARE_NO = ${careNo}

SELECT MEMBER_ID memberId, REGTIME regtime, CARE_NO careNo, REGDATE regdate, TO_CHAR(INSERT_DATE, 'YYYY-MM-DD') insertDate, PEOPLE people
FROM RESERVATION
WHERE REGDATE = #{regdate}
AND CARE_NO = #{careNo}
AND REGTIME = #{regtime}
AND MEMBER_ID = #{memberId}
